mapper.select:
SELECT *
FROM chatroom
WHERE member_id = #{member_id} 
	AND maker_member_id = #{maker_member_id}

SELECT *
    FROM chat
    WHERE room_id = #{room_id}
    ORDER BY chat_idx DESC
    LIMIT #{startRow }, #{listLimit }

SELECT COUNT(chat_idx)
	FROM chat
	WHERE room_id = #{room_id}

SELECT COUNT(room_id)
FROM chatroom
WHERE maker_member_id = #{sId}
