mapper.insert:

	SELECT IFNULL(MAX(notice_idx), 0) FROM notice


INSERT INTO notice
	VALUES (
		#{notice_idx} + 1
		, #{notice_category}
		, #{notice_name}
		, #{notice_subject}
		, #{notice_content}
		, #{notice_thumnail}
		, #{notice_file}
		, #{notice_idx} + 1 -- notice_re_ref
		, 0 -- notice_re_lev
		, 0 -- notice_re_seq
		, 0 -- notice_readcount
		, now()
	)

mapper.update:

	SELECT notice_readcount
		FROM notice
			WHERE notice_idx = #{notice_idx}


UPDATE notice
	SET notice_readcount = notice_readcount + 1
		WHERE notice_idx = #{notice_idx}
