mapper.select:
SELECT maker_idx
	FROM maker
	WHERE member_idx = (
	    SELECT member_idx
	    FROM members
	    WHERE member_id = #{sId}
	);

	SELECT COUNT(*)
    FROM mission
    WHERE maker_idx = #{maker_idx}
    AND mission_name = #{mission_name}

mapper.insert:
    INSERT INTO mission (maker_idx, mission_name, points)
	VALUES (
	    #{maker_idx},
	    #{mission_name},
	    #{points}
	)
	ON DUPLICATE KEY UPDATE points = IF(VALUES(points) > points, VALUES(points), points);

mapper.update:
      	UPDATE maker
	    SET maker_grade =
	        CASE
	            WHEN (
	                SELECT SUM(points)
	                FROM mission
	                WHERE maker_idx = #{maker_idx}
	            ) >= 10 THEN 4
	            WHEN (
	                SELECT SUM(points)
	                FROM mission
	                WHERE maker_idx = #{maker_idx}
	            ) >= 7 THEN 3
	            WHEN (
	                SELECT SUM(points)
	                FROM mission
	                WHERE maker_idx = #{maker_idx}
	            ) >= 5 THEN 2
	            ELSE 1
	        END
	    WHERE maker_idx = #{maker_idx};
