mapper.select:
SELECT COUNT(*)
FROM email
WHERE email = #{email}

mapper.update:
UPDATE email
SET authCode = #{authCode}
WHERE email = #{email}

mapper.select:
SELECT COUNT(*)
	FROM email
	WHERE email = #{email} and authCode = #{authCode}

mapper.update:
UPDATE members
SET false_count = #{false_count}
WHERE member_id = #{member_id}

mapper.select:
SELECT COUNT(*)
	FROM members
	WHERE member_email = #{email}

mapper.update:
UPDATE members
	SET member_passwd = #{passwd}
	WHERE member_email = #{email}

mapper.select:
SELECT *
FROM members
WHERE member_idx = (
    SELECT member_idx
    FROM maker
    JOIN project ON maker.maker_idx = project.maker_idx
    WHERE project.project_idx = #{project_idx}
);

mapper.update:
UPDATE payment
SET delivery_status = 3
WHERE payment_idx = #{payment_idx}
