mapper.select:
SELECT * 
	FROM delivery
		WHERE delivery_idx = #{delivery_idx}
			AND member_id = #{id}

SELECT pay.payment_idx , pro.project_subject , pay.payment_quantity , pay.total_amount , pay.delivery_status , re.reward_name , re.reward_detail , re.reward_option , pay.payment_date , del.delivery_add , del.delivery_detailadd , pay.waybill_num , pay.payment_confirm , pay.waybill_num , rev.review_idx , pro.project_idx , pay. FROM payment pay JOIN project pro ON pay.project_idx = pro.project_idx JOIN reward re ON re.reward_idx = pay.reward_idx JOIN delivery del ON del.delivery_idx = pay.delivery_idx LEFT JOIN review rev ON pay.payment_idx = rev.payment_idx WHERE pay.member_idx = (SELECT member_idx FROM members WHERE member_id = #{sId })
AND payment_confirm = #{payment_confirm}
ORDER BY pay.payment_idx DESC LIMIT #{startRow}, #{listLimit}

mapper.update:
	UPDATE payment
    SET cancel_context = #{context}, cancel_img = #{saveFileName}, payment_confirm = 3
    WHERE payment_idx = #{payment_idx}

mapper.select:
SELECT count(*)
	FROM payment
		WHERE project_idx = #{project_idx} AND payment_confirm = 1;

SELECT *
	FROM project
		WHERE project_subject = #{searchKeyword}
			OR project_hashtag = #{searchKeyword}
